"use strict";
let sa_ajax_url;
let sa_interval_id;
let sa_status = 'stopped';
let sa_datatables;
window.addEventListener('load', function() {
    console.log('Loading The SEO Workspace view..');
    if (typeof weAreInServicesAnimals !== 'undefined') {
        sa_ajax_url = document.getElementById('sa_form').dataset.sa_ajax_url;
    }
    jQuery('body').on('click', '.sa-wrap a[href="#"]', function (e) {
        e.preventDefault();
    });
    jQuery('body').on('click', '.sa-btn-see-results', function () {
        let newTab = window.open('/wp-admin/tools.php?page=the-seo-machine', '_blank');
    });
    jQuery('body').on('click', '.sa-btn-study-site', function () {
        if (sa_status == 'stopped') {
            saStudySite();
        } else {
            saStopAll();
        }
    });
    saLoadDatatables();
});
function saStudySite() {
    console.log('Starting study site..');
    document.getElementById('sa-btn-study-site').innerHTML = 'Stop';
    document.getElementById('sa-btn-study-site').classList.add('sa-button-studying');
    let quantity_per_batch = document.getElementById('quantity_per_batch').value;
    let time_between_batches = document.getElementById('time_between_batches').value;
    console.log('Quantity per batch: ' + quantity_per_batch + ', time between batches: ' + time_between_batches);
    sa_interval_id = setInterval(saStudySiteSendAjax, time_between_batches * 1000);
    sa_status = 'studying';
}
function saStudySiteSendAjax() {
    document.getElementById('sa-box-study-site-status').innerHTML = 'Doing batch..';
    let xhr = new XMLHttpRequest();
    xhr.onreadystatechange = function (response) {
        if (xhr.readyState === 4) {
            document.getElementById('sa-box-study-site-status').innerHTML = 'Studying with current status: <strong>' + xhr.responseText + '</strong>';
            saUpdateStatusBar();
            if (xhr.responseText.includes('finished')) {
                saStopAll();
            }
        }
    }
    xhr.open('POST', sa_ajax_url);
    xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded; charset=UTF-8');
    xhr.send('action=sa_do_batch&site-id=' + document.getElementById('sa-current-selected-site-id').value);
}
function saUpdateStatusBar() {
    if(document.getElementById('sa-current-selected-site-id')) {
        let xhr = new XMLHttpRequest();
        xhr.onreadystatechange = function (response) {
            if (xhr.readyState === 4) {
                let num_urls_in_queue = xhr.responseText.split(',')[0];
                let num_urls_in_queue_visited = xhr.responseText.split(',')[1];
                let num_urls = xhr.responseText.split(',')[2];
                document.getElementById('sa-progress-queue-text').innerHTML = num_urls_in_queue_visited
                    + ' URLs studied from the queue out of a total of ' + num_urls_in_queue + ' URLs enqueued';
                document.getElementById('sa-progress-queue-content').style.width = (num_urls_in_queue_visited * 100 / num_urls_in_queue) + '%';
            }
        }
        xhr.open('POST', sa_ajax_url);
        xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded; charset=UTF-8');
        xhr.send('action=sa_get_status&site-id=' + document.getElementById('sa-current-selected-site-id').value);
    }
}
function saStopAll() {
    clearInterval(sa_interval_id);
    document.getElementById('sa-btn-study-site').innerHTML = 'Study Site';
    document.getElementById('sa-btn-study-site').classList.remove('sa-button-studying');
    sa_status = 'stopped';
}
function saLoadDatatables() {
    jQuery('#sa-datatable tfoot th.filterme').each(function () {
        let title = jQuery(this).text();
        jQuery(this).html('<input type="text" placeholder="Filter.." />');
    })
    sa_datatables = jQuery('#sa-datatable').DataTable({
        dom: '<"float-left"i><"float-right"f>t<"float-left"l>B<"float-right"p><"clearfix">',
        responsive: true,
        order: [[0, "desc"]],
        buttons: ['csv', 'excel', 'pdf'],
        initComplete: function () {
            this.api().columns().every(function () {
                let that = this
                jQuery('input', this.footer()).on('keyup change', function () {
                    if (that.search() !== this.value) {
                        that
                            .search(this.value)
                            .draw()
                    }
                })
            })
        },
        processing: true,
        serverSide: true,
        ajax: {
            url: sa_ajax_url + '?action=sa_urls',
            type: 'POST'
        },
        columnDefs: [
            { 'name': 'id', 'targets': 0 },
            { 'name': 'home_url', 'targets': 1 },
            { 'name': 'mainInfo', 'targets': 2 },
            { 'name': 'is_online', 'targets': 3 },
            { 'name': 'emails_to_notify', 'targets': 4 },
            {
                'name': 'actions',
                'targets': 5,
                'data': null,
                'defaultContent': 'Show Edit',
                'render': function (data, type, row, meta) {
                    return '<a href="?page=services-animals&select-id=' + data[0] + '" class="button button-green sa-btn-select-item">Select</a>' +
                        '<a href="?page=services-animals&edit-id=' + data[0] + '" class="button button-green sa-btn-edit-item">Edit</a>';
                }
            }
        ],
        aLengthMenu: [[10, 25, 50, 100, 500, 1000, 2000, 5000, 10000, -1], [10, 25, 50, 100, 500, 1000, 2000, 5000, 10000, "All"]]
    });
    console.log('Loaded The SEO Workspace view!');
}
